var catalogbuy=document.querySelectorAll(".buy");var catalogbuypopup=document.querySelector(".modal-content-cart");var catalogbuyclose=catalogbuypopup.querySelector(".modal-content-close");var catalogbuychekout=catalogbuypopup.querySelector(".checkout");var catalogbuycontshopp=catalogbuypopup.querySelector(".contshopp");for(var i=0;i<catalogbuy.length;i++){catalogbuy[i].addEventListener("click",function(event){event.preventDefault();catalogbuypopup.classList.add("modal-content-show")})}catalogbuychekout.addEventListener("click",function(event){event.preventDefault();catalogbuypopup.classList.remove("modal-content-show")});catalogbuycontshopp.addEventListener("click",function(event){event.preventDefault();catalogbuypopup.classList.remove("modal-content-show")});catalogbuyclose.addEventListener("click",function(event){event.preventDefault();catalogbuypopup.classList.remove("modal-content-show")});window.addEventListener("keydown",function(event){if(event.keyCode===27){if(catalogbuypopup.classList.contains("modal-content-show")){catalogbuypopup.classList.remove("modal-content-show")}}});
